(91, 90, 1, 1, 'bledixhindi', 'progresioni.py', '<pre>#Funksioni per gjetje e shumes se n termave te pare te nje progresioni aritmetik\n\ndef  shuma(a,  d,  n): \n    sum = (n / 2) * (2 * a + (n - 1) * d)  #Formula per gjetjen e shumes se n termave \n    return sum\n    \nn = int(input(\"Vendosni numrin e n termave te progresionit:\")) \n \na = 1   #Numri i pare i progresionit\nd = 3   #Ndryshesa ndermjet numrave te progresionit\n\nprint(\"Shuma e\" n \"termave te pare te progresionit eshte:\"shuma(a, d, n))</pre><p><br/></p>', '185.89.159.80', 1633621031, 0),
(92, 91, 1, 1, 'bledixhindi', 'makina_llogaritese.py', '<pre>#---------------------------------------------\n# Funksionet matematikore\ndef shuma(a, b):\n    return(a + b)\ndef zbritje(a,b):\n    return (a - b)\ndef prodhimi(a, b):\n    return (a * b)\ndef raporti(a, b):\n    return (a / b)\ndef fuqi(a, b) :\n    return (a ** b)\n#---------------------------------------------------------\n#Mesazhi hyres\nprint(\'--------------------------------\nnveprimet e mundshme jane:nshuma: \n+nzbritje: -nprodhimi: *nraporti: /nngritje ne fuqi:\n ^n-------------------------------\')\n#----------------------------------------------------------\nn = input(\'vendos numrat dhe veprimin e deshiruar ne formen \"a + b\":  \')\nm = n.split()\n#---------------------------------------------------------\n\nif m[1] == \'+\':\n    print(\'= \'+str(shuma(int(m[0]),int(m[2]))))\nelif m[1] == \'-\':\n    print(\'= \'+str(zbritje(int(m[0]),int(m[2]))))\nelif m[1] == \'*\':\n    print(\'= \'+str(prodhimi(int(m[0]),int(m[2]))))\nelif m[1] == \'/\':\n    print(\'= \'+str(raporti(int(m[0]),int(m[2]))))\nelif m[1] == \'^\':\n    print(\'= \'+str(fuqi(int(m[0]),int(m[2]))))\nelse:\n    print(\'vendos numra natyrore dhe simbolet e pershatshme\')\n\n# nga Kristain Blido</pre><p><br/></p>', '185.89.159.80', 1633621062, 0),
(93, 92, 1, 1, 'bledixhindi', 'Elektriciteti', '<p style=\"line-height: 16px;\"><img style=\"vertical-align: middle; margin-right: 2px;\" src=\"/static/editor/dialogs/attachment/fileTypeImages/icon_pdf.gif\"/><a style=\"font-size:12px; color:#0066cc;\" href=\"/upload/file/20211007/1633621121752637.pdf\" title=\"texstudio_elektriciteti.pdf\">texstudio_elektriciteti.pdf</a></p><p><br/></p>', '185.89.159.80', 1633621129, 0),
