(153, 152, 1, 1, 'bledixhindi', 'Database studentor', '<pre class=\"brush:cpp;toolbar:false\">#include &lt;iostream&gt;\n#include &lt;cstdio&gt;\n#include &lt;cstring&gt;\n#include &lt;cstdlib&gt;\n#include &lt;conio.h&gt;\n#include &lt;iomanip&gt;\n\nusing namespace std;\nint main()\n{\n    FILE *fp, *ft;\n    char another, choice;\n\n    struct student\n    {\n        char first_name[50], last_name[50];\n        char course[100];\n        int section;\n    };\n\n    struct student e;\n    char xfirst_name[50], xlast_name[50];\n    long int recsize;\n\n    fp=fopen(\"users.txt\",\"rb+\");\n\n    if (fp == NULL)\n    {\n        fp = fopen(\"users.txt\",\"wb+\");\n\n        if (fp==NULL)\n        {\n            puts(\"Cannot open file\");\n            return 0;\n        }\n    }\n\n\n    recsize = sizeof(e);\n\n    while(1)\n    {\n        system(\"cls\");\n\n        cout &lt;&lt; \"\\t\\t====== STUDENT DATABASE MANAGEMENT SYSTEM ======\";\n        cout &lt;&lt;\"\\n\\n                                          \";\n        cout &lt;&lt; \"\\n\\n\";\n        cout &lt;&lt; \"\\n \\t\\t\\t 1. Add    Records\";\n        cout &lt;&lt; \"\\n \\t\\t\\t 2. List   Records\";\n        cout &lt;&lt; \"\\n \\t\\t\\t 3. Modify Records\";\n        cout &lt;&lt; \"\\n \\t\\t\\t 4. Delete Records\";\n        cout &lt;&lt; \"\\n \\t\\t\\t 5. Exit   Program\";\n        cout &lt;&lt; \"\\n\\n\";\n        cout &lt;&lt; \"\\t\\t\\t Select Your Choice :=&gt; \";\n        fflush(stdin);\n        choice = getche();\n        switch(choice)\n        {\n        case \'1\' :\n            fseek(fp,0,SEEK_END);\n            another =\'Y\';\n            while(another == \'Y\' || another == \'y\')\n            {\n                system(\"cls\");\n                cout &lt;&lt; \"Enter the Firt Name : \";\n                cin &gt;&gt; e.first_name;\n                cout &lt;&lt; \"Enter the Last Name : \";\n                cin &gt;&gt; e.last_name;\n                cout &lt;&lt; \"Enter the Course    : \";\n                cin &gt;&gt; e.course;\n                cout &lt;&lt; \"Enter the Section   : \";\n                cin &gt;&gt; e.section;\n                fwrite(&amp;e,recsize,1,fp);\n                cout &lt;&lt; \"\\n Add Another Record (Y/N) \";\n                fflush(stdin);\n                another = getchar();\n            }\n            break;\n        case \'2\':\n            system(\"cls\");\n            rewind(fp);\n            cout &lt;&lt; \"=== View the Records in the Database ===\";\n            cout &lt;&lt; \"\\n\";\n            while (fread(&amp;e,recsize,1,fp) == 1)\n            {\n                cout &lt;&lt; \"\\n\";\n                cout &lt;&lt;\"\\n\" &lt;&lt; e.first_name &lt;&lt; setw(10)  &lt;&lt; e.last_name;\n                cout &lt;&lt; \"\\n\";\n                cout &lt;&lt;\"\\n\" &lt;&lt;e.course &lt;&lt;  setw(8)  &lt;&lt; e.section;\n            }\n            cout &lt;&lt; \"\\n\\n\";\n            system(\"pause\");\n            break;\n\n        case \'3\' :\n            system(\"cls\");\n            another = \'Y\';\n            while (another == \'Y\'|| another == \'y\')\n            {\n                cout &lt;&lt; \"\\n Enter the last name of the student : \";\n                cin &gt;&gt; xlast_name;\n\n                rewind(fp);\n                while (fread(&amp;e,recsize,1,fp) == 1)\n                {\n                    if (strcmp(e.last_name,xlast_name) == 0)\n                    {\n                        cout &lt;&lt; \"Enter new the Firt Name : \";\n                        cin &gt;&gt; e.first_name;\n                        cout &lt;&lt; \"Enter new the Last Name : \";\n                        cin &gt;&gt; e.last_name;\n                        cout &lt;&lt; \"Enter new the Course    : \";\n                        cin &gt;&gt; e.course;\n                        cout &lt;&lt; \"Enter new the Section   : \";\n                        cin &gt;&gt; e.section;\n                        fseek(fp, - recsize, SEEK_CUR);\n                        fwrite(&amp;e,recsize,1,fp);\n                        break;\n                    }\n                    else\n                        cout&lt;&lt;\"record not found\";\n                }\n                cout &lt;&lt; \"\\n Modify Another Record (Y/N) \";\n                fflush(stdin);\n                another = getchar();\n            }\n            break;\n\n\n        case \'4\':\n            system(\"cls\");\n            another = \'Y\';\n            while (another == \'Y\'|| another == \'y\')\n            {\n                cout &lt;&lt; \"\\n Enter the last name of the student to delete : \";\n                cin &gt;&gt; xlast_name;\n\n                ft = fopen(\"temp.dat\", \"wb\");\n\n                rewind(fp);\n                while (fread (&amp;e, recsize,1,fp) == 1)\n\n                    if (strcmp(e.last_name,xlast_name) != 0)\n                    {\n                        fwrite(&amp;e,recsize,1,ft);\n                    }\n                fclose(fp);\n                fclose(ft);\n                remove(\"users.txt\");\n                rename(\"temp.dat\",\"users.txt\");\n\n                fp=fopen(\"users.txt\",\"rb+\");\n\n                cout &lt;&lt; \"\\n Delete Another Record (Y/N) \";\n                fflush(stdin);\n                another = getchar();\n            }\n\n            break;\n\n        case \'5\':\n            fclose(fp);\n            cout &lt;&lt; \"\\n\\n\";\n            cout &lt;&lt; \"\\t\\t     THANK YOU FOR USING THIS SOFTWARE\";\n            cout &lt;&lt; \"\\n\\n\";\n            exit(0);\n        }\n    }\n\n\n    system(\"pause\");\n    return 0;\n}</pre><p><br/></p>', '185.89.159.80', 1633870302, 0),
(154, 153, 1, 1, 'bledixhindi', 'Makine llogaritese vetem per mbledhjet', '<pre class=\"brush:cpp;toolbar:false\">#include &lt;iostream&gt;\nusing namespace std;\nint main()\n{  int x, y;  int sum;  cout &lt;&lt; \"Vendos nje numer: \"; \ncin &gt;&gt; x;  cout &lt;&lt; \"Numri i 2-te: \";\n  cin &gt;&gt; y;  sum = x + y; \ncout &lt;&lt; \"Kaq del bir: \" &lt;&lt; sum; \nreturn 0;}</pre><p><br/></p>', '185.89.159.80', 1633870333, 0),
